for(var dataset=[],numDataPoints=30,xRange=1e3*Math.random(),yRange=1e3*Math.random(),i=0;numDataPoints>i;i++){var newNumber1=Math.round(Math.random()*xRange),newNumber2=Math.round(Math.random()*yRange);dataset.push([newNumber1,newNumber2])}var w=600,h=300,padding=35,xScale=d3.scale.linear().domain([0,d3.max(dataset,function(a){return a[0]})]).range([padding,w-3*padding]),yScale=d3.scale.linear().domain([0,d3.max(dataset,function(a){return a[1]})]).range([h-padding,padding]),xAxis=d3.svg.axis().scale(xScale).orient("bottom").ticks(5),yAxis=d3.svg.axis().scale(yScale).orient("left").ticks(5),svg=d3.select("body").append("svg").attr("width",w).attr("height",h);svg.selectAll("circle").data(dataset).enter().append("circle").attr("cx",function(a){return xScale(a[0])}).attr("cy",function(a){return yScale(a[1])}).attr("r",5).attr("opacity",function(a){return xScale(a[0])/1e3}),svg.append("g").attr("class","axis").attr("transform","translate(0,"+(h-padding)+")").call(xAxis),svg.append("g").attr("class","axis").attr("transform","translate("+padding+", 0)").call(yAxis);